    private void btnLoginActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnLoginActionPerformed
        String message;
        String titulo;
        char[] password = ptxtContrasenia.getPassword();
        String checkPassword = new String(password);
        if (txtUsuario.getText().equals("alumno@ulp.edu.ar") && checkPassword.equals("12345678")) {
            message = "Bienvenido alumno";
            titulo = "Registro correcto";
        } else {
            message = "Usuario y/o contrase単a incorrectos.";
            titulo = "Error";
        }
        JOptionPane.showMessageDialog(
            this,
            message,
            titulo,
            JOptionPane.INFORMATION_MESSAGE
        );
    }//GEN-LAST:event_btnLoginActionPerformed

